import React, { Component } from 'react';
import axios from 'axios';
import ImageResults from "../imageResults/imageResults";

class Search extends Component {
  state = {
    searchText: '',
    apiUrl: 'https://pixabay.com/api',
    apiKey: '17241914-90da7b93c0ccceb734849dcd1',
    images: [],
    searchClicked: false
  };

  onTextChange = (e) => {
    const val = e.target.value;
    this.setState({ [e.target.name]: val });
  };

  onSearchClick = () => {
    axios
      .get(
        `${this.state.apiUrl}/?key=${this.state.apiKey}&q=${this.state.searchText}&image_type=photo&safesearch=true`
      )
      .then(res => this.setState({ images: res.data.hits, searchClicked: true }))
      .catch(err => console.log(err));
  };

  render() {
    console.log(this.state.images);
    return (
        <>
        <div className="cont">
            <h1 style={{color: 'white', textAlign:'center'}}>IMAGE SEARCH APP</h1>
        </div>
      <div 
    //   style={{display: 'flex'}}
      >
        <input
          type="text"
          style={{
            // marginLeft:570,
            // marginTop:100,
            // paddingTop:20,
            // paddingLeft:70,
            // fontSize:30,
            // borderTopStyle:"hidden",
            // borderRightStyle:"hidden",
            // borderLeftStyle:"hidden",
            // outline:"none",
            // borderBottomStyle:"groove",
            backgroundColor: 'black',
            color: 'white',
            marginLeft: '500px',
            marginTop: '30px',
            paddingTop: '10px',
            paddingLeft: '70px',
            paddingBottom: '10px',
            fontSize: '30px',
            border: '1px solid white',
            
          }}
          placeholder="Search for images"
          name="searchText"
          value={this.state.searchText}
          onChange={this.onTextChange}
        />
        <button onClick={this.onSearchClick} 
        style={{
           marginTop: '30px',
           backgroundColor: 'black',
           color: 'white',
           border: '1px solid white',
           paddingTop: '23px',
           paddingBottom: '13px',
           paddingLeft: '20px',
           paddingRight: '20px',
           fontSize: '16px',
           borderRadius: '1px',
           cursor: 'pointer'
        }}>Search</button>
        <br />
        {this.state.searchClicked && this.state.images.length > 0 ? (<ImageResults images={this.state.images} />) : null}
      </div>
      </>
    );
  }
}

export default Search;
