import React, { Component } from 'react';
import axios from 'axios';
import ImageResults from "../imageResults/imageResults";
import ActionSearch from 'material-ui/svg-icons/action/search';


class Search extends Component {
  state = {
    searchText: '',
    apiUrl: 'https://pixabay.com/api',
    // apiKey: '17241914-90da7b93c0ccceb734849dcd1',
    apiKey: process.env.IMAGE_API,
    images: [],
    searchClicked: false
  };

  onTextChange = (e) => {
    const val = e.target.value;
    this.setState({ [e.target.name]: val });
  };

  onSearchClick = () => {
    axios
      .get(
        `${this.state.apiUrl}/?key=${this.state.apiKey}&q=${this.state.searchText}&image_type=photo&safesearch=true`
      )
      .then(res => this.setState({ images: res.data.hits, searchClicked: true }))
      .catch(err => console.log(err));
  };

  render() {
    console.log(this.state.images);
    return (
        <>
        <div className="cont" style={{paddingTop: '15px'}}>
            <h1 style={{color: 'black', textAlign:'center',  fontFamily: 'Raleway', fontSize: '35px', fontWeight: '600', marginBottom: '2px'}}>IMAGE  SEARCH  APP</h1>
        </div>
      <div>
        <input
          type="text"
          style={{
            backgroundColor: 'black',
            color: 'white',
            marginLeft: '500px',
            marginTop: '30px',
            padding: '7px 70px 7px 70px',
            fontSize: '30px',
            border: '3px solid white',  
            borderRadius: '30px',
            textAlign: 'center' 
          }}
          placeholder="Search For Images"
          name="searchText"
          value={this.state.searchText}
          onChange={this.onTextChange}
        />
        <button onClick={this.onSearchClick} 
        style={{
           marginTop: '30px',
           marginLeft: '10px',
           backgroundColor: '#2eb82e',
           color: 'white',
           border: '3px solid black',
           padding: '10px 12px 10px 12px',
           fontSize: '16px',
           borderRadius: '30px',
           cursor: 'pointer'
        }}>
            <ActionSearch></ActionSearch>
            {/* <ImageSearchIcon color="white"></ImageSearchIcon> */}
            </button>
        <br />
        {this.state.searchClicked && this.state.images.length > 0 ? (<ImageResults images={this.state.images} />) : null}
      </div>
      </>
    );
  }
}

export default Search;
