import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { GridList, GridTile } from 'material-ui/GridList';
import IconButton from 'material-ui/IconButton';
import ZoomIn from 'material-ui/svg-icons/action/zoom-in';
import Dialog from 'material-ui/Dialog';
import FlatButton from 'material-ui/FlatButton';

const ImageResults = ({ images }) => {
  const [open, setOpen] = useState(false);
  const [currentImg, setCurrentImg] = useState('');

  const handleOpen = (img) => {
    setOpen(true);
    setCurrentImg(img);
  };

  const handleClose = () => {
    setOpen(false);
  };

  let imageList;

  if (images) {
    imageList = (
      <GridList
        cols={window.innerWidth < 768 ? 1 : 4}
        cellHeight={300}
        style={{ width: '100%', overflowY: 'auto' }}
      >
        {images.map((img) => (
          <GridTile
            title={img.tags}
            key={img.id}
            actionIcon={
              <IconButton onClick={() => handleOpen(img.largeImageURL)}>
                <ZoomIn color="white" />
              </IconButton>
            }
            style={{ cursor: 'pointer' }}
          >
            <img src={img.largeImageURL} alt="" style={{ width: '100%' }} />
          </GridTile>
        ))}
      </GridList>
    );
  } else {
    imageList = null;
  }

  const actions = [
    <FlatButton label="Close" primary={true} onClick={handleClose} />
  ];

  return (
    <div style={{ margin: '20px auto', maxWidth: '800px' }}>
      {imageList}
      <Dialog
        actions={actions}
        modal={false}
        open={open}
        onRequestClose={handleClose}
      >
        <img src={currentImg} alt="" style={{ width: '100%' }} />
      </Dialog>
    </div>
  );
};

ImageResults.propTypes = {
  images: PropTypes.array.isRequired
};

export default ImageResults;
